import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Button from '@material-ui/core/Button';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import ReactJson from 'react-json-view';
import axios, { AxiosResponse } from 'axios';

const useStyles = makeStyles({
  root: {
    width: 700,
    margin: 20,
    padding: 10,
    overflowWrap: 'anywhere'
  },
  title: {
    fontWeight: 'bold'
  },
  subject: {
    marginTop: 20
  },
  firstName: {
    marginRight: 10
  },
  lastName: {
    marginLeft: 10
  },
  form: {
    marginTop: 20
  },
  submit: {
    width: 100,
    marginTop: 10
  }
});

interface DID {
  did: string;
};

const ApplicationBar: React.FC = () => {
  const classes = useStyles();
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [issuerDID, setIssuerDID] = useState("");
  const [subjectDID, setSubjectDID] = useState("");
  const [confirmed, setConfirmed] = useState(false);
  const [vc, setVC] = useState({});

  const submitAvailable = () => issuerDID && subjectDID && firstName && lastName && confirmed;

  const handleCreateDID = () => {
    axios.post('/api/did')
    .then((response: AxiosResponse<DID>) => {
      setSubjectDID(response.data.did);
    })
    .catch((error) => {
      console.log(error);
    });
  }

  const handleSubmit = () => {
    axios.post('/api/vc', {
      firstName,
      lastName,
      subjectDID
    })
    .then((response: AxiosResponse) => {
      setVC(response.data.vc);
    })
    .catch((error) => {
      console.log(error);
    });
  }

  useEffect(() => {
    axios.get('/api/issuer/did')
    .then((response: AxiosResponse<DID>) => {
      setIssuerDID(response.data.did);
    })
    .catch((error) => {
      console.log(error);
    });
  }, []);



  return (
      <Card className={classes.root}>
        {!Object.keys(vc).length ? 
          <CardContent>
            <Typography className={classes.title} variant="h5">Limit Order</Typography>
            <Typography variant="subtitle1">Token1: MATIC</Typography>
            <TextField 
              id="first-name" 
              data-testid="first-name" 
              className={classes.firstName} 
              label="First Name" 
              variant="standard" 
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}/>
            <Typography variant="subtitle1">Token2: USDT</Typography>
            <TextField 
              id="last-name" 
              data-testid="last-name" 
              className={classes.lastName} 
              label="Last Name" 
              variant="standard" 
              onChange={(e) => setLastName(e.target.value)}/>

            <FormGroup className={classes.form}>
              <Button 
                className={classes.submit} 
                variant="contained" 
                color="primary" 
                data-testid="submit"
                disabled={submitAvailable() ? false : true}
                onClick={handleSubmit}>
                  SUBMIT
              </Button>
            </FormGroup>
          </CardContent>
        : <CardContent>
            <ReactJson src={vc}/>
          </CardContent>
        }
      </Card>
  );
};

export default ApplicationBar;
